        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1485178, 'Rifapentine', 'Paclitaxel (protein-bound)', 'Moderate', 'Theoretically, coadministration with drugs that are inducers of CYP450 2C8 and/or 3A4 may decrease the plasma concentrations of paclitaxel, which is metabolized by these isoenzymes.', NULL, 'Clinicians should recognize the potential for interaction with drugs that induce CYP450 2C8 and/or 3A4 and monitor for evidence of reduced therapeutic response to paclitaxel during coadministration.', NULL, 'Clinicians should recognize the potential for interaction with drugs that induce CYP450 2C8 and/or 3A4 and monitor for evidence of reduced therapeutic response to paclitaxel during coadministration.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
INSERT OR IGNORE INTO drug_interactions (
