    ) VALUES (1485188, 'Riociguat', 'Rifapentine', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of riociguat, which is partially metabolized by the isoenzyme.', NULL, 'The potential for diminished therapeutic effects of riociguat should be considered when prescribed in combination with CYP450 3A4 inducers.', NULL, 'The potential for diminished therapeutic effects of riociguat should be considered when prescribed in combination with CYP450 3A4 inducers. Patients should be closely monitored, and the dosage of riociguat adjusted as necessary. Data are not available to guide dosing of riociguat when coadministered with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort. Alternative agents with no or minimal CYP450 3A4-inducing activity should be considered whenever possible.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1485189, 'Ripretinib', 'Rifapentine', 'Major', 'Coadministration with strong inducers of CYP450 3A4 may decrease the plasma concentrations and anti-tumor activities of ripretinib and its active metabolite (DP-5439). The proposed mechanism is induction of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of ripretinib and DP-5439.', NULL, 'Until more information is available, concomitant use of ripretinib with a strong CYP450 3A4 inducer is not recommended.', NULL, 'Until more information is available, concomitant use of ripretinib with a strong CYP450 3A4 inducer is not recommended. Loss of anti-tumor activity may occur.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
