        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1485193, 'Ropivacaine', 'Rifapentine', 'Minor', 'Rifampin or other rifamycins may decrease the plasma concentrations of ropivacaine. The mechanism is rifamycin induction of CYP450 1A2 and 3A4, the isoenzymes responsible for the metabolic clearance of ropivacaine.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
