        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1485194, 'Rosiglitazone', 'Rifapentine', 'Moderate', 'Coadministration with inducers of CYP450 2C8 may decrease the plasma concentrations of rosiglitazone, which is primarily metabolized by the isoenzyme.', NULL, 'Because the antidiabetic effect of rosiglitazone is dose- and concentration-dependent, patients treated concomitantly with a CYP450 2C8 inducer may require a higher dosage of rosiglitazone.', NULL, 'Because the antidiabetic effect of rosiglitazone is dose- and concentration-dependent, patients treated concomitantly with a CYP450 2C8 inducer may require a higher dosage of rosiglitazone. Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of the CYP450 2C8 inducer, and the rosiglitazone dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
