        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1485994, 'Ticlopidine', 'Roflumilast', 'Moderate', 'Coadministration with CYP450 1A2 inhibitors or dual CYP450 1A2/3A4 inhibitors may increase the systemic exposure to roflumilast and its pharmacologically active N-oxide metabolite.', NULL, 'Treatment with roflumilast should be re-evaluated if an interaction is suspected and persistent intolerability occurs.', NULL, 'Treatment with roflumilast should be re-evaluated if an interaction is suspected and persistent intolerability occurs. Patients should be advised to contact their physician if they experience increased frequency and/or severity of side effects such as diarrhea, nausea, abdominal pain, weight loss, and headache.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
