INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1486458, 'Dicoumarol', 'Rosuvastatin', 'Moderate', 'Rosuvastatin may enhance the hypoprothrombinemic effect of warfarin and other oral anticoagulants. The mechanism of interaction is unknown.', 'DDInter', 1767317477, 'Caution is recommended if rosuvastatin is used concomitantly with oral anticoagulants. The INR should be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation, discontinuation or change of dosage of rosuvastatin in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is recommended if rosuvastatin is used concomitantly with oral anticoagulants.', NULL, NULL, 'Moderate', 'pharmacodynamic');
