INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1486498, 'Rosuvastatin', 'Ruxolitinib', 'Moderate', 'Coadministration with ruxolitinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein efflux transporter and/or the breast cancer resistance protein (BRCP) in the intestine. The mechanism is decreased clearance via these routes due to inhibition by ruxolitinib.', 'DDInter', 1767317477, 'Caution is advised if ruxolitinib must be used concurrently with medications that are substrates of the P-glycoprotein efflux transporter and/or the breast cancer resistance protein (BRCP) , particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ruxolitinib is added to or withdrawn from therapy. The interaction may possibly be avoided or minimized if the doses of the affected medications are separated for as long as possible.', 'Excretion', 'Caution is advised if ruxolitinib must be used concurrently with medications that are substrates of the P-glycoprotein efflux transporter and/or the breast cancer resistance protein (BRCP) , particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
