INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1486500, 'Simeprevir', 'Rosuvastatin', 'Major', 'Coadministration with simeprevir may significantly increase the plasma concentrations of rosuvastatin. The proposed mechanism is simeprevir inhibition of the hepatic uptake of rosuvastatin via organic anion transporting polypeptide 1B1 (OATP1B1).', 'DDInter', 1767317477, 'Rosuvastatin therapy should be initiated at a dosage of 5 mg once daily, and not exceed a dosage of 10 mg daily, when prescribed with simeprevir. Alternatively, fluvastatin may be substituted, as it has been shown to have no significant interaction with simeprevir. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'Rosuvastatin therapy should be initiated at a dosage of 5 mg once daily, and not exceed a dosage of 10 mg daily, when prescribed with simeprevir.', NULL, NULL, 'Major', 'pharmacodynamic');
