INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1486502, 'Tafamidis', 'Rosuvastatin', 'Moderate', 'Based on in vitro data, coadministration with tafamidis may increase the plasma concentrations and the risk of toxicity of drugs that are substrates of the transport protein breast cancer resistance protein (BCRP) (e.g., methotrexate, rosuvastatin, imatinib), uridine diphosphate glucuronosyltransferase (UGT) 1A1 (e.g., bictegravir, irinotecan, raltegravir), and/or uptake transporters OAT1 and OAT3 (organic anion transporters) (e.g., bumetanide, furosemide, lamivudine, methotrexate, oseltamivir, tenofovir, ganciclovir, adefovir, cidofovir, zidovudine, zalcitabine) at clinically relevant concentrations. The proposed mechanism is decreased clearance due to tafamidis-mediated inhibition of the corresponding transport protein.', 'DDInter', 1767317477, 'Caution is advised if tafamidis is used concomitantly with drugs that are substrates of these transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever tafamidis is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Excretion', 'Caution is advised if tafamidis is used concomitantly with drugs that are substrates of these transport proteins, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1486503, 'Telbivudine', 'Rosuvastatin', 'Moderate', 'Prolonged use of certain nucleoside reverse transcriptase inhibitors may commonly cause myopathy, including rare cases of rhabdomyolysis. It is not known if the risk of myopathy is increased during concurrent administration of other agents associated with myopathy, including corticosteroids, fibric acid derivatives, HMG-CoA reductase inhibitors, chloroquine, hydroxychloroquine, penicillamine, and lipid-lowering doses of niacin.', 'DDInter', 1767317477, 'Clinicians should carefully consider the benefits versus risks prior to prescribing these agents together. Patients should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever.', 'Synergism', 'Clinicians should carefully consider the benefits versus risks prior to prescribing these agents together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1486504, 'Temsirolimus', 'Rosuvastatin', 'Moderate', 'Higher dosages of lipid-lowering agents may be required during concomitant administration of sirolimus or temsirolimus. Dose-related serum lipid abnormalities, including but not limited to increased serum cholesterol and triglycerides, have been frequently reported in patients treated with sirolimus or temsirolimus.', 'DDInter', 1767317477, 'Close clinical monitoring of serum cholesterol and triglycerides is recommended if sirolimus or temsirolimus is prescribed to patients receiving lipid-lowering agents. Modification of the antihyperlipidemic regimen or dosages may be necessary.', 'Others', 'Close clinical monitoring of serum cholesterol and triglycerides is recommended if sirolimus or temsirolimus is prescribed to patients receiving lipid-lowering agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
