INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1487296, 'Fostamatinib', 'Sacituzumab govitecan', 'Major', 'Coadministration of sacituzumab govitecan with inhibitors of uridine diphosphate glucuronosyltransferase 1A1 (UGT1A1) may increase the risk of adverse effects, such as nausea, vomiting, diarrhea, and neutropenia. The proposed mechanism involves increased exposure to the pharmacologically active metabolite SN-38, a topoisomerase I inhibitor linked to sacituzumab govitecan, which is metabolized via UGT1A1. Genetic variants of the UGT1A1 gene such as the UGT1A1*28 allele lead to reduced UGT1A1 enzyme activity and patients who are homozygous for the UGT1A1*28 allele are at increased risk for neutropenia from SN-38.', 'DDInter', 1767317477, 'Concomitant use of sacituzumab govitecan with UGT1A1 inhibitors should be avoided. If coadministration is unavoidable, patients should be closely monitored for the development of severe nausea, vomiting, diarrhea, and neutropenia. Patients should be advised to promptly report symptoms such as chills, fever, malaise, sore throat, mouth sores, unusual fatigue, bruising, or bleeding.', 'Synergism', 'Concomitant use of sacituzumab govitecan with UGT1A1 inhibitors should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
