INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1487399, 'Solriamfetol', 'Safinamide', 'Major', 'Coadministration of solriamfetol with other drugs that also inhibit the reuptake of dopamine or norepinephrine, including monoamine oxidase inhibitors (MAOIs), may increase the risk of hypertensive reactions including death, stroke, myocardial infarction, aortic dissection, pulmonary edema, and renal failure. Additionally, MAOIs can increase dopaminergic and noradrenergic activity by inhibiting the metabolism of dopamine and norepinephrine.', 'DDInter', 1767317477, 'Concomitant use of solriamfetol with MAOIs is considered contraindicated. At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with solriamfetol.', 'Metabolism, Synergism', 'Concomitant use of solriamfetol with MAOIs is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1487400, 'Sotalol', 'Safinamide', 'Moderate', 'Sympathetic ganglion-blocking or catecholamine-depleting agents such as guanethidine, reserpine, and monoamine oxidase (MAO) inhibitors may potentiate the pharmacologic effects of beta-blockers, which are thought to competitively antagonize catecholamines at cardiac and other peripheral adrenergic neurons. Combining these medications may increase the risk of hypotension, orthostasis, bradycardia, and heart failure due to excessive reduction of sympathetic activity.', 'DDInter', 1767317477, 'Caution is advised if beta-blockers, including ophthalmic formulations, are prescribed in combination with sympathetic ganglion-blocking or catecholamine-depleting agents. Patients should contact their doctor if they experience dizziness, lightheadedness, syncope, bradycardia, shortness of breath, difficulty breathing, edema, and/or chest pain.', 'Synergism', 'Caution is advised if beta-blockers, including ophthalmic formulations, are prescribed in combination with sympathetic ganglion-blocking or catecholamine-depleting agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1487401, 'St. John''s Wort', 'Safinamide', 'Major', 'By inhibiting serotonin metabolism, monoamine oxidase inhibitors (MAOIs), including safinamide, may potentiate the pharmacologic activity of serotonergic agents including serotonin norepinephrine reuptake inhibitors (SNRIs), St. John''s wort, cyclobenzaprine, trazodone, and tricyclic and tetracyclic antidepressants. The result may be an increased risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Clinically, the interaction has been reported primarily in patients treated with MAOIs (including reversible, irreversible, selective, and nonselective) and tricyclic antidepressants, especially imipramine and clomipramine, which are the most potent serotonin reuptake inhibitors of the class. Symptoms of the serotonin syndrome may include mental status changes such as irritability, altered consciousness, confusion, hallucinations, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.', 'DDInter', 1767317477, 'Coadministration of safinamide with these serotonergic agents is considered contraindicated.', 'Synergism', 'Coadministration of safinamide with these serotonergic agents is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1487402, 'Sulfasalazine', 'Safinamide', 'Moderate', 'Coadministration with safinamide may increase the plasma concentrations of drugs that are substrates of the breast cancer resistance protein (BCRP) transporter. The proposed mechanism is decreased clearance due to intestinal BCRP-inhibition by safinamide.', 'DDInter', 1767317477, 'Caution is advised when safinamide is prescribed with drugs that are BCRP substrates, particularly those with a narrow therapeutic range such as methotrexate, topotecan, and irinotecan. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever safinamide is added to or withdrawn from therapy.', 'Others', 'Caution is advised when safinamide is prescribed with drugs that are BCRP substrates, particularly those with a narrow therapeutic range such as methotrexate, topotecan, and irinotecan.', NULL, NULL, 'Moderate', 'pharmacodynamic');
