INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1487702, 'Foscarnet', 'Samarium (153Sm) lexidronam', 'Moderate', 'Coadministration with other drugs that can reduce serum calcium may potentiate the risk of hypocalcemia associated with foscarnet. Foscarnet chelates with divalent metal ions and causes a dose-related reduction in ionized serum calcium.', 'DDInter', 1767317477, 'Close monitoring is recommended during concomitant use of foscarnet with other drugs that are known to affect calcium concentrations. Serum creatinine and electrolyte levels should be assessed prior to initiating therapy and frequently during therapy. Patients should be advised to promptly notify their physician if they experience potential symptoms of hypocalcemia such as perioral tingling, numbness in extremities, and paresthesias.', 'Distribution', 'Close monitoring is recommended during concomitant use of foscarnet with other drugs that are known to affect calcium concentrations.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1487703, 'Ganciclovir', 'Samarium (153Sm) lexidronam', 'Moderate', 'The use of ganciclovir with other potentially myelotoxic and/or nephrotoxic agents may increase the risk and severity of hematologic toxicity and/or nephrotoxicity due to both additive pharmacodynamic effects and additive effects on the kidney. Ganciclovir alone may cause severe leucopenia, neutropenia, anemia, thrombocytopenia, pancytopenia, bone marrow depression, aplastic anemia, increased serum creatinine levels, and acute renal failure, and the risk may theoretically increase when coadministered with other myelotoxic or nephrotoxic therapy.', 'DDInter', 1767317477, 'Ganciclovir or its prodrug, valganciclovir, should generally not be combined with other drugs associated with myelosuppression and/or nephrotoxicity unless the benefits are anticipated to outweigh the potential risks. Extreme caution is advised if they are used in patients who have recently received or are receiving treatment with myelotoxic and/or nephrotoxic drugs, and vice versa. If coadministration is required, reduced dosages of one or more of the drugs may be required, and the patient should be monitored for the development of hematologic and/or renal adverse effects both during and after discontinuation of therapy.', 'Synergism', 'Ganciclovir or its prodrug, valganciclovir, should generally not be combined with other drugs associated with myelosuppression and/or nephrotoxicity unless the benefits are anticipated to outweigh the potential risks.', NULL, NULL, 'Moderate', 'pharmacodynamic');
