INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1487824, 'Zinc chloride', 'Sarecycline', 'Moderate', 'INTERVAL: The bioavailability of oral tetracyclines may be significantly decreased during concurrent administration with zinc-containing products. Therapeutic failure may result. The proposed mechanism is chelation of tetracyclines by the zinc cation, forming an insoluble complex that is poorly absorbed from the gastrointestinal tract.', 'DDInter', 1767317477, 'Patients receiving an oral tetracycline in combination with zinc-containing products should be advised to separate the times of administration by at least three to four hours. Doxycycline may be an appropriate alternative, since its absorption may not be significantly affected by zinc coadministration.', 'Absorption', 'Patients receiving an oral tetracycline in combination with zinc-containing products should be advised to separate the times of administration by at least three to four hours.', NULL, NULL, 'Moderate', 'pharmacodynamic');
