INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1488084, 'Edrophonium', 'Scopolamine', 'Moderate', 'Anticholinergic agents and other agents with significant anticholinergic activity (e.g., clozapine, class IA antiarrhythmics especially disopyramide) may antagonize the effects of cholinergic skeletal muscle stimulants (e.g., ambenonium, edrophonium, guanidine, neostigmine, pyridostigmine). Although this interaction may be desirable in some situations, such as when atropine is used to treat excessive muscarinic side effects and cholinergic crisis induced by anticholinesterase overdose, unintentional or indiscriminate use of anticholinergic agents in the treatment of myasthenia gravis may exacerbate symptoms.', 'DDInter', 1767317477, 'Agents with potent anticholinergic activity should preferably be avoided in patients receiving cholinergic skeletal muscle stimulants. If concurrent use is necessary, patients treated for myasthenia gravis should be monitored for potential exacerbation of symptoms.', 'Antagonism', 'Agents with potent anticholinergic activity should preferably be avoided in patients receiving cholinergic skeletal muscle stimulants.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1488085, 'Esketamine', 'Scopolamine', 'Moderate', 'Concomitant use of esketamine with central nervous system (CNS) depressants may increase sedation and impairment of attention, judgment, thinking, reaction speed, and psychomotor skills.', 'DDInter', 1767317477, 'Caution is advised and patients should be closely monitored during concomitant use of esketamine with CNS depressants. Due to the risk of delayed or prolonged sedation and other adverse effects, patients should be monitored for at least 2 hours after esketamine administration, followed by an assessment to determine when the patient is considered clinically stable and ready to leave the healthcare setting. Patients should be instructed not to engage in potentially hazardous activities, such as driving a motor vehicle or operating machinery, until the next day after a restful sleep.', 'Synergism', 'Caution is advised and patients should be closely monitored during concomitant use of esketamine with CNS depressants.', NULL, NULL, 'Moderate', 'pharmacodynamic');
