INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1488272, 'Secobarbital', 'Quinidine', 'Moderate', 'Some barbiturates may significantly decrease serum quinidine concentrations and half-life. The mechanism is induction of CYP450 3A4 hepatic metabolism of quinidine. Data are available for pentobarbital, phenobarbital, and primidone.', 'DDInter', 1767317477, 'Monitor the effectiveness and serum levels of quinidine until a stable, safe, and therapeutic level is achieved after a barbiturate is added or deleted from this patient''s regimen. Dose adjustments may be required. Patients should be advised to notify their physician if they experience worsening irregular heartbeats or exacerbation of other symptoms. Patients should also be observed for quinidine toxicity after the barbiturate is discontinued.', 'Metabolism', 'Monitor the effectiveness and serum levels of quinidine until a stable, safe, and therapeutic level is achieved after a barbiturate is added or deleted from this patient''s regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
