INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1488749, 'Selpercatinib', 'Magaldrate', 'Major', 'Coadministration with drugs that increase gastric pH may significantly decrease the oral bioavailability of selpercatinib and reduce its concentrations in plasma.', 'DDInter', 1767317477, 'Concomitant use of selpercatinib with locally-acting antacids or other agents with acid neutralizing or reducing effects should generally be avoided. If coadministration is required, the manufacturer recommends taking selpercatinib 2 hours before or 2 hours after the antacid.', 'Absorption', 'Concomitant use of selpercatinib with locally-acting antacids or other agents with acid neutralizing or reducing effects should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
