INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1488829, 'Troglitazone', 'Selpercatinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of selpercatinib, which is primarily metabolized by the isoenzyme. When selpercatinib was coadministered with multiple doses of rifampin, a potent CYP450 3A4 inducer, selpercatinib peak plasma concentration (Cmax) and systemic exposure (AUC) decreased by 70% and 87%, respectively.', 'DDInter', 1767317477, 'The potential for diminished pharmacologic effects of selpercatinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of selpercatinib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1488830, 'Troleandomycin', 'Selpercatinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of selpercatinib, which is primarily metabolized by the isoenzyme. Increased exposure to selpercatinib may increase the risk of serious adverse effects such as QT interval prolongation, liver transaminase and bilirubin elevations, hypertension, hemorrhage, edema, and hypersensitivity reactions (e.g., fever, rash, arthralgias/myalgias with concurrent decreased platelets or transaminitis).', 'DDInter', 1767317477, 'Concomitant use of selpercatinib with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends decreasing the dosage of selpercatinib. Patients receiving selpercatinib 120 mg twice daily should have the dosage reduced to 40 mg twice daily, and those receiving 160 mg twice daily should have the dosage reduced to 80 mg twice daily. Close monitoring for adverse effects is advisable, including more frequent ECGs and laboratory monitoring of liver enzymes, bilirubin, electrolytes, glucose, and blood counts. Selpercatinib treatment should be discontinued, interrupted, or dosage reduced in patients with serious or life-threatening toxicities in accordance with the product labeling. After the inhibitor has been discontinued for 3 to 5 elimination half-lives, the selpercatinib dosage that was taken prior to initiating the inhibitor may be resumed.', 'Metabolism', 'Concomitant use of selpercatinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
