INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1488943, 'Chlorpromazine', 'Sertraline', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', 'DDInter', 1767317477, 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1488944, 'Choline salicylate', 'Sertraline', 'Moderate', 'Serotonin reuptake inhibitors (SRIs) may potentiate the risk of bleeding in patients treated with ulcerogenic agents and agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The tricyclic antidepressant, clomipramine, is also a strong SRI and may interact similarly. Serotonin release by platelets plays an important role in hemostasis, thus SRIs may alter platelet function and induce bleeding.', 'DDInter', 1767317477, 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1488945, 'Cisapride', 'Sertraline', 'Major', 'Cisapride can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Coadministration of cisapride with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', 'Coadministration of cisapride with other drugs that can prolong the QT interval is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1488946, 'Cyproheptadine', 'Sertraline', 'Moderate', 'Serotonin antagonists such as cyproheptadine and methysergide may diminish the pharmacologic effects of selective serotonin reuptake inhibitors (SSRIs) and other highly serotonergic agents (e.g., 5-hydroxytryptophan; buspirone; mirtazapine; nefazodone; sibutramine; St. John''s wort; trazodone; SNRIs such as duloxetine, desvenlafaxine, milnacipran, and venlafaxine). This pharmacodynamic interaction is sometimes exploited in the use of cyproheptadine and methysergide for the treatment of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Theoretically, serotonergic agents may also antagonize the pharmacologic effects of cyproheptadine and methysergide, although this effect has not been reported.', 'DDInter', 1767317477, 'Patients should be monitored for potentially diminished therapeutic response to serotonergic agents during coadministration with cyproheptadine or methysergide, and dosages adjusted as necessary.', 'Antagonism', 'Patients should be monitored for potentially diminished therapeutic response to serotonergic agents during coadministration with cyproheptadine or methysergide, and dosages adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1488947, 'Danaparoid', 'Sertraline', 'Moderate', 'Serotonin reuptake inhibitors may potentiate the risk of bleeding in patients receiving heparin therapy. The exact mechanism is unknown but may involve a pharmacodynamic interaction, as serotonin reuptake inhibitors have been reported to interfere with platelet function.', 'DDInter', 1767317477, 'Caution is recommended if a serotonin reuptake inhibitor is administered with heparin therapy, especially in the elderly and patients with renal impairment. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is recommended if a serotonin reuptake inhibitor is administered with heparin therapy, especially in the elderly and patients with renal impairment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
