INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1489066, 'Sertraline', 'Phenylbutazone', 'Moderate', 'Serotonin reuptake inhibitors (SRIs) may potentiate the risk of bleeding in patients treated with ulcerogenic agents and agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The tricyclic antidepressant, clomipramine, is also a strong SRI and may interact similarly. Serotonin release by platelets plays an important role in hemostasis, thus SRIs may alter platelet function and induce bleeding.', 'DDInter', 1767317477, 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1489067, 'Sertraline', 'Phenylpropanolamine', 'Major', 'Serotonin reuptake inhibitors may potentiate the pharmacologic response to sympathomimetic agents.', 'DDInter', 1767317477, 'In general, amphetamines and other sympathomimetic appetite suppressants should not be combined with selective serotonin reuptake inhibitors (SSRIs) or serotonin-norepinephrine reuptake inhibitors (SNRIs). Close monitoring for enhanced sympathomimetic effects and possible serotonin syndrome is recommended if these agents must be used together.', 'Synergism', 'In general, amphetamines and other sympathomimetic appetite suppressants should not be combined with selective serotonin reuptake inhibitors (SSRIs) or serotonin-norepinephrine reuptake inhibitors (SNRIs).', NULL, NULL, 'Major', 'pharmacodynamic');
