INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1489144, 'Troglitazone', 'Sertraline', 'Moderate', 'Coadministration with troglitazone may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is accelerated clearance due to induction of CYP450 3A4 activity by troglitazone.', 'DDInter', 1767317477, 'Caution is advised if troglitazone must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever troglitazone is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if troglitazone must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1489145, 'Troleandomycin', 'Sertraline', 'Moderate', 'The use of some macrolide antibiotics has been associated with increased blood levels of some selective serotonin reuptake inhibitors (SSRIs), resulting in excessive serotonergic effects or serotonin syndrome. The mechanism is inhibition of the CYP450 3A4 isoenzyme, which metabolizes SSRIs.', 'DDInter', 1767317477, 'In cases where both drugs are necessary, one could consider using a macrolide antibiotic that does not inhibit CYP450 3A4, such as azithromycin or dirithromycin.', 'Metabolism', 'In cases where both drugs are necessary, one could consider using a macrolide antibiotic that does not inhibit CYP450 3A4, such as azithromycin or dirithromycin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1489146, 'Tryptophan', 'Sertraline', 'Major', 'Concomitant use of agents with serotonergic activity such as serotonin reuptake inhibitors and tryptophan may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Symptoms of the serotonin syndrome may include mental status changes such as irritability, altered consciousness, confusion, hallucinations, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.', 'DDInter', 1767317477, 'The concomitant use of serotonin reuptake inhibitors and tryptophan should be avoided.', 'Synergism', 'The concomitant use of serotonin reuptake inhibitors and tryptophan should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
