INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1489287, 'Bepridil', 'Sildenafil', 'Moderate', 'Phosphodiesterase-5 (PDE5) inhibitors may potentiate the blood pressure-lowering effect of antihypertensive medications. These agents inhibit PDE5-mediated degradation of cyclic guanosine monophosphate (cGMP), which in vascular smooth muscles can lead to peripheral vasodilation.', 'DDInter', 1767317477, 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents. Patients receiving the combination should be advised to avoid rising abruptly from a sitting or recumbent position, especially following treatment initiation or a dosage increase, and to contact their doctor if they experience symptoms of hypotension such as dizziness, lightheadedness, fainting, or tachycardia.', 'Synergism', 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1489288, 'Berotralstat', 'Sildenafil', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of sildenafil, which is primarily metabolized by the isoenzyme. The possibility of prolonged and/or increased pharmacologic effects of sildenafil should be considered.', 'DDInter', 1767317477, 'Caution is advised if sildenafil is coadministered with CYP450 3A4 inhibitors. Dosage adjustments may be appropriate for sildenafil whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy based on efficacy and side effects. Patients should be advised to promptly notify their physician if they experience pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', 'Caution is advised if sildenafil is coadministered with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1489289, 'Boceprevir', 'Sildenafil', 'Major', 'Coadministration with protease inhibitors (PIs), especially ritonavir, may significantly increase the plasma concentrations of sildenafil. The mechanism is PI inhibition of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sildenafil.', 'DDInter', 1767317477, 'A safe and effective dosage of sildenafil has not been established for the treatment of pulmonary arterial hypertension when used in the presence of protease inhibitors. Due to the potential for increased sildenafil-associated adverse events including visual disturbances, hypotension, prolonged erection and syncope, this use is considered contraindicated. When used for erectile dysfunction, a lower starting dose of 25 mg should be considered for patients treated with saquinavir. For patients receiving other PIs, the maximum dosage of sildenafil should not exceed a single dose of 25 mg in any given 48-hour period. Patients should be advised to promptly notify their doctor if they experience pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', 'A safe and effective dosage of sildenafil has not been established for the treatment of pulmonary arterial hypertension when used in the presence of protease inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1489290, 'Butabarbital', 'Sildenafil', 'Moderate', 'Theoretically, coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of sildenafil, which is primarily metabolized by the isoenzyme. The possibility of a diminished therapeutic response to sildenafil should be considered.', 'DDInter', 1767317477, 'Pharmacologic response to sildenafil should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the sildenafil dosage adjusted if necessary.', 'Metabolism', 'Pharmacologic response to sildenafil should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the sildenafil dosage adjusted if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
