    ) VALUES (1489594, 'St. John''s Wort', 'Simeprevir', 'Major', 'Coadministration with potent and some moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of simeprevir, which is primarily metabolized by the isoenzyme.', NULL, 'The use of simeprevir in combination with potent and some moderate CYP450 3A4 inducers such as carbamazepine, dexamethasone, efavirenz, enzalutamide, eslicarbazepine, etravirine, nevirapine, oxcarbazepine, phenobarbital, phenytoin, primidone, rifamycins, St.', NULL, 'The use of simeprevir in combination with potent and some moderate CYP450 3A4 inducers such as carbamazepine, dexamethasone, efavirenz, enzalutamide, eslicarbazepine, etravirine, nevirapine, oxcarbazepine, phenobarbital, phenytoin, primidone, rifamycins, St. John''s wort, and tipranavir should generally be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
