    ) VALUES (1489675, 'Stiripentol', 'Simvastatin', 'Moderate', 'The concomitant administration of stiripentol may increase plasma concentrations and toxic effects of certain hepatically metabolized drugs including ergot alkaloids, quinidine, immunosuppressants, and statins. The mechanism is inhibition of CYP450 3A4 metabolism by stiripentol.', NULL, 'The manufacturer recommends that concomitant use should be avoided, unless strictly necessary.', NULL, 'The manufacturer recommends that concomitant use should be avoided, unless strictly necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
