    ) VALUES (1489902, 'Typhoid vaccine (inactivated)', 'Siponimod', 'Moderate', 'INTERVAL: The administration of inactivated, killed, or otherwise noninfectious vaccines during siponimod therapy is generally safe but may be associated with a diminished or suboptimal immunologic response. Vaccination may be less effective during and for up to one month after discontinuation of siponimod therapy.', NULL, 'Immune response to vaccines may be reduced during and for up to one month after discontinuation of siponimod therapy.', NULL, 'Immune response to vaccines may be reduced during and for up to one month after discontinuation of siponimod therapy. Treatment with siponimod should be paused 1 week prior to and for 4 weeks after vaccination.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
