        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1490077, 'Cabotegravir', 'Sodium bicarbonate', 'Moderate', 'INTERVAL: Coadministration with antacids may decrease the bioavailability of oral cabotegravir. The proposed mechanism likely involves a pH-dependent reduction in dissolution and absorption of cabotegravir, which is poorly soluble over a wide pH range. The interaction has not been studied specifically with antacids.', NULL, 'To minimize the risk of reduced viral susceptibility and resistance development associated with subtherapeutic levels of cabotegravir, antacids or other medications that contain antacids (e.', NULL, 'To minimize the risk of reduced viral susceptibility and resistance development associated with subtherapeutic levels of cabotegravir, antacids or other medications that contain antacids (e.g., didanosine buffered tablets or pediatric oral solution) should be administered at least 2 hours before or 4 hours after the oral cabotegravir dose.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
