    ) VALUES (1490095, 'Aminoglutethimide', 'Solifenacin', 'Moderate', 'Theoretically, coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of solifenacin, which has been shown to be a substrate of the isoenzyme in vitro.', NULL, 'Pharmacologic response to solifenacin should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the solifenacin dosage adjusted if necessary.', NULL, 'Pharmacologic response to solifenacin should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the solifenacin dosage adjusted if necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
