INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1490252, 'Troglitazone', 'Solifenacin', 'Moderate', 'Theoretically, coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of solifenacin, which has been shown to be a substrate of the isoenzyme in vitro.', 'DDInter', 1767317477, 'Pharmacologic response to solifenacin should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the solifenacin dosage adjusted if necessary.', 'Metabolism', 'Pharmacologic response to solifenacin should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the solifenacin dosage adjusted if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1490253, 'Troleandomycin', 'Solifenacin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of solifenacin, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'The dosage of solifenacin should not exceed 5 mg/day when used with potent CYP450 3A4 inhibitors. Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be advised to contact their physician if they experience undue adverse effects of solifenacin such as severe abdominal pain or constipation for 3 or more days. Patients should seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope. Concomitant use of solifenacin with potent CYP450 3A4 inhibitors, such as itraconazole, is considered contraindicated by some authorities in patients with severe renal impairment or moderate to severe hepatic impairment during and for 2 weeks after treatment with these drugs.', 'Metabolism', 'The dosage of solifenacin should not exceed 5 mg/day when used with potent CYP450 3A4 inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
