INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1490426, 'Sorafenib', 'Paclitaxel (protein-bound)', 'Moderate', 'Concomitant administration of paclitaxel and sorafenib may increase the plasma concentrations of both drugs. The mechanism by which paclitaxel increases sorafenib exposure is unknown; sorafenib may increase paclitaxel exposure by inhibiting its metabolism via the CYP450 2C8 enzymatic pathway.', 'DDInter', 1767317477, 'Caution is advised during the concomitant use of paclitaxel and sorafenib. Close monitoring for toxicities of paclitaxel (e.g., bone marrow suppression, especially neutropenia; conduction abnormalities; peripheral neuropathy) and sorafenib (e.g., rash/desquamation; hand-foot skin reaction; hypertension; hemorrhage; cardiac ischemia; myocardial infarction; QT prolongation; diarrhea; anorexia; gastrointestinal perforation; wound healing complications) is recommended, and the dosage of each drug adjusted as necessary. No dosage adjustment should be needed when paclitaxel is coadministered with sorafenib following a 3-day break in sorafenib dosing.', 'Metabolism', 'Caution is advised during the concomitant use of paclitaxel and sorafenib.', NULL, NULL, 'Moderate', 'pharmacodynamic');
