INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1490762, 'Fedratinib', 'Stiripentol', 'Moderate', 'Coadministration with dual CYP450 3A4 and 2C19 inhibitors may increase the plasma concentrations of fedratinib, which is a substrate of these isoenzymes.', 'DDInter', 1767317477, 'According to the product labeling, concomitant use of fedratinib with dual CYP450 3A4 and 2C19 inhibitors should be avoided.', 'Metabolism', 'According to the product labeling, concomitant use of fedratinib with dual CYP450 3A4 and 2C19 inhibitors should be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
