        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1491052, 'Aluminum hydroxide', 'Sucralfate', 'Moderate', 'INTERVAL: Antacid-induced gastric pH changes may reduce binding of sucralfate with the gastrointestinal mucosa resulting in reduced therapeutic efficacy. In addition, aluminum may accumulate in patients with renal failure who are concomitantly taking sucralfate with aluminum-containing antacids.', NULL, 'Sucralfate and antacid doses should be separated by at least half an hour.', NULL, 'Sucralfate and antacid doses should be separated by at least half an hour.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
