    ) VALUES (1491053, 'Anisindione', 'Sucralfate', 'Moderate', 'INTERVAL: Sucralfate may interfere with the absorption of warfarin in some patients and may reduce its therapeutic effect. The mechanism is unknown and clinical data have been conflicting.', NULL, 'Patients should be advised to take the anticoagulant at least 2 hours before or 6 hours after the sucralfate to minimize a potential interaction.', NULL, 'Patients should be advised to take the anticoagulant at least 2 hours before or 6 hours after the sucralfate to minimize a potential interaction. Close observation for altered anticoagulant effect is recommended. Sucralfate may interact in the same manner with other oral anticoagulants as well.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
