        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1491056, 'Chlorpromazine', 'Sucralfate', 'Minor', 'Coadministration with aluminum- or magnesium-containing antacids may decrease the serum concentrations of orally administered phenothiazines. The proposed mechanism is antacid adsorption resulting in reduced phenothiazine bioavailability.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
