    ) VALUES (1491187, 'Tioguanine', 'Sulfasalazine', 'Moderate', 'Aminosalicylate derivatives (e.g., balsalazide, mesalamine, olsalazine, sulfasalazine) may potentiate the pharmacologic effects of purine antagonist antimetabolites (e.g., azathioprine, mercaptopurine, thioguanine).', NULL, 'Caution is advised if aminosalicylate derivatives must be used concomitantly with purine antagonist antimetabolites.', NULL, 'Caution is advised if aminosalicylate derivatives must be used concomitantly with purine antagonist antimetabolites. Patients receiving the combination should be closely monitored for hematologic toxicity, and the antimetabolite dosage adjusted accordingly.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
