    ) VALUES (1491654, 'Caspofungin', 'Tacrolimus', 'Moderate', 'Caspofungin may reduce the plasma concentrations of tacrolimus. The mechanism of this potential interaction is unknown. In vitro studies indicate that caspofungin is not an inhibitor of any enzyme in the CYP450 system.', NULL, 'During concomitant therapy, tacrolimus blood levels should be monitored as usual and the dosage adjusted accordingly, particularly following initiation or discontinuation of caspofungin therapy in patients who are stabilized on tacrolimus.', NULL, 'During concomitant therapy, tacrolimus blood levels should be monitored as usual and the dosage adjusted accordingly, particularly following initiation or discontinuation of caspofungin therapy in patients who are stabilized on tacrolimus.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
