INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1491894, 'Berotralstat', 'Tadalafil', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of tadalafil, which is primarily metabolized by the isoenzyme. The possibility of prolonged and/or increased pharmacologic effects of tadalafil should be considered.', 'DDInter', 1767317477, 'Caution is advised if tadalafil is prescribed with CYP450 3A4 inhibitors. Dosage adjustments may be appropriate for tadalafil whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy based on efficacy and side effects. Tadalafil labeling recommends that the dosage not exceed 10 mg once every 72 hours in patients treated concomitantly with a potent CYP450 3A4 inhibitor, such as erythromycin, itraconazole, ketoconazole, protease inhibitors, and nefazodone. Patients should be advised to promptly notify their physician if they experience potential symptoms of PDE5 inhibitor toxicity such as pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', 'Caution is advised if tadalafil is prescribed with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1491895, 'Boceprevir', 'Tadalafil', 'Major', 'Coadministration with the hepatitis C virus (HCV) NS3/4A protease inhibitors, boceprevir and telaprevir, may significantly increase the plasma concentrations of tadalafil. The mechanism involves inhibition of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of tadalafil. The interaction has not been specifically studied with either boceprevir or telaprevir, but has been reported with other potent CYP450 3A4 inhibitors such as ketoconazole or ritonavir.', 'DDInter', 1767317477, 'A safe and effective dosage of tadalafil has not been established for the treatment of pulmonary arterial hypertension when used in the presence of HCV NS3/4A protease inhibitors. Due to the potential for increased tadalafil-associated adverse events including visual disturbances, hypotension, prolonged erection and syncope, this use is considered contraindicated. When used for erectile dysfunction, the maximum dosage of tadalafil should not exceed a single dose of 10 mg in any given 72-hour period. Patients should be advised to promptly notify their doctor if they experience pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', 'A safe and effective dosage of tadalafil has not been established for the treatment of pulmonary arterial hypertension when used in the presence of HCV NS3/4A protease inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
