INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1491979, 'Tafamidis', 'Artesunate', 'Moderate', 'Based on in vitro data, coadministration with tafamidis may increase the plasma concentrations and the risk of toxicity of drugs that are substrates of the transport protein breast cancer resistance protein (BCRP) (e.g., methotrexate, rosuvastatin, imatinib), uridine diphosphate glucuronosyltransferase (UGT) 1A1 (e.g., bictegravir, irinotecan, raltegravir), and/or uptake transporters OAT1 and OAT3 (organic anion transporters) (e.g., bumetanide, furosemide, lamivudine, methotrexate, oseltamivir, tenofovir, ganciclovir, adefovir, cidofovir, zidovudine, zalcitabine) at clinically relevant concentrations. The proposed mechanism is decreased clearance due to tafamidis-mediated inhibition of the corresponding transport protein.', 'DDInter', 1767317477, 'Caution is advised if tafamidis is used concomitantly with drugs that are substrates of these transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever tafamidis is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Excretion', 'Caution is advised if tafamidis is used concomitantly with drugs that are substrates of these transport proteins, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1491980, 'Belinostat', 'Tafamidis', 'Moderate', 'Coadministration with inhibitors of UGT1A1 may increase the plasma concentrations of belinostat, which is primarily (80% to 90%) metabolized by UGT1A1-mediated glucuronidation.', 'DDInter', 1767317477, 'Concomitant use of belinostat with potent UGT1A1 inhibitors such as atazanavir should generally be avoided. Caution is advised when used with other agents that may also inhibit UGT1A1 such as nilotinib, pazopanib, regorafenib and sorafenib. Patients should be monitored closely for potentially increased adverse effects such as nausea, vomiting, diarrhea, thrombocytopenia, leukopenia (neutropenia and lymphopenia), anemia, infection, and hepatotoxicity.', 'Metabolism', 'Concomitant use of belinostat with potent UGT1A1 inhibitors such as atazanavir should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
