INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1492077, 'Venetoclax', 'Talazoparib', 'Moderate', 'Coadministration with venetoclax may increase the plasma concentrations of drugs that are substrates of P-glycoprotein (P-gp). The proposed mechanism is decreased clearance due to inhibition of P-glycoprotein-mediated drug efflux in the intestine, liver, and/or kidney by venetoclax.', 'DDInter', 1767317477, 'Caution is advised when venetoclax is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range such as digoxin and dabigatran etexilate. Alternatives should be considered whenever possible. The manufacturer recommends that drugs sensitive to P-gp inhibition in the gastrointestinal tract be administered at least 6 hours before venetoclax. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate following the initiation or discontinuation of venetoclax.', 'Others', 'Caution is advised when venetoclax is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range such as digoxin and dabigatran etexilate.', NULL, NULL, 'Moderate', 'pharmacodynamic');
