INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1492353, 'Tasimelteon', 'Acebutolol', 'Moderate', 'INTERVAL: Coadministration with beta-blockers may diminish the therapeutic effects of tasimelteon. Beta-blockers have been shown to reduce the production of melatonin via inhibition of beta-1 adrenergic receptors; therefore, the efficacy of tasimelteon, which acts as an agonist at melatonin MT1 and MT2 receptors, may be reduced.', 'DDInter', 1767317477, 'Product labeling for tasimelteon suggests avoiding the nighttime administration of beta-blockers.', 'Antagonism', 'Product labeling for tasimelteon suggests avoiding the nighttime administration of beta-blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1492354, 'Aminoglutethimide', 'Tasimelteon', 'Moderate', 'Coadministration with inducers of CYP450 1A2 and/or 3A4 may decrease the plasma concentrations of tasimelteon, which is primarily metabolized by these isoenzymes.', 'DDInter', 1767317477, 'The potential for diminished therapeutic effects of tasimelteon should be considered during coadministration with inducers of CYP450 1A2 and/or 3A4. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished therapeutic effects of tasimelteon should be considered during coadministration with inducers of CYP450 1A2 and/or 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1492355, 'Amoxapine', 'Tasimelteon', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317477, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
