INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1492436, 'Tasimelteon', 'Morphine (liposomal)', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', 'DDInter', 1767317477, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery.', 'Synergism', 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1492437, 'Tasimelteon', 'Nadolol', 'Moderate', 'INTERVAL: Coadministration with beta-blockers may diminish the therapeutic effects of tasimelteon. Beta-blockers have been shown to reduce the production of melatonin via inhibition of beta-1 adrenergic receptors; therefore, the efficacy of tasimelteon, which acts as an agonist at melatonin MT1 and MT2 receptors, may be reduced.', 'DDInter', 1767317477, 'Product labeling for tasimelteon suggests avoiding the nighttime administration of beta-blockers.', 'Antagonism', 'Product labeling for tasimelteon suggests avoiding the nighttime administration of beta-blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
