INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1492475, 'Timolol', 'Tasimelteon', 'Moderate', 'INTERVAL: Coadministration with beta-blockers may diminish the therapeutic effects of tasimelteon. Beta-blockers have been shown to reduce the production of melatonin via inhibition of beta-1 adrenergic receptors; therefore, the efficacy of tasimelteon, which acts as an agonist at melatonin MT1 and MT2 receptors, may be reduced.', 'DDInter', 1767317477, 'Product labeling for tasimelteon suggests avoiding the nighttime administration of beta-blockers.', 'Antagonism', 'Product labeling for tasimelteon suggests avoiding the nighttime administration of beta-blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
