INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1492536, 'Etravirine', 'Tazemetostat', 'Major', 'Coadministration with moderate or potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of tazemetostat, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inducers should generally be avoided. Alternative therapeutic agents with less enzyme induction potential should be considered whenever possible.', 'Metabolism', 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1492537, 'Everolimus', 'Tazemetostat', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may decrease the blood concentrations and pharmacologic effects of everolimus.', 'DDInter', 1767317477, 'Some manufacturers recommend avoiding concomitant use of everolimus with moderate CYP450 3A4 and/or P-gp inducers such as bosentan, efavirenz, and nevirapine. If concomitant use is required, a dose adjustment of everolimus should be considered to achieve the recommended therapeutic range for the condition being treated. Everolimus whole blood trough levels should be closely monitored during treatment, particularly 2 weeks after a dose increase and 2 weeks after discontinuation of the inducer.', 'Metabolism', 'Some manufacturers recommend avoiding concomitant use of everolimus with moderate CYP450 3A4 and/or P-gp inducers such as bosentan, efavirenz, and nevirapine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1492538, 'Exemestane', 'Tazemetostat', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of exemestane, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'The manufacturer recommends increasing the dosage of exemestane to 50 mg once daily when used with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort. However, it has also been suggested that suppression of estrogen levels is not affected by the interaction, thus dosage adjustment of exemestane is not required. The extent to which other, less potent CYP450 3A4 inducers may interact with exemestane is unknown. Caution is advised if they are used with exemestane.', 'Metabolism', 'The manufacturer recommends increasing the dosage of exemestane to 50 mg once daily when used with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1492539, 'Fedratinib', 'Tazemetostat', 'Major', 'Coadministration with moderate or potent CYP450 3A4 inhibitors may significantly increase the plasma concentrations of tazemetostat, which is primarily metabolized by the isoenzyme. Clinically, this interaction may result in an increased risk of the frequency or severity of adverse reactions due to tazemetostat such as secondary malignancies, hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress.', 'DDInter', 1767317477, 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inhibitors should generally be avoided. Alternative therapeutic agents with less inhibitory potential should be considered whenever possible. If coadministration with a moderate CYP450 3A4 inhibitor is required and no other alternatives are possible, the dose of tazemetostat should be reduced as per manufacturer recommendations. After the inhibitor has been discontinued for 3 elimination half-lives, the tazemetostat dose that was taken prior to initiating the inhibitor may be resumed. Patients should be advised to notify their health care professional if they experience signs or symptoms of possible tazemetostat-related adverse effects including secondary malignancies, hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress.', 'Metabolism', 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1492540, 'Felbamate', 'Tazemetostat', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of tazemetostat, which is primarily metabolized by the isoenzyme. This may lead to reduced efficacy of tazemetostat.', 'DDInter', 1767317477, 'Caution and clinical monitoring is recommended if tazemetostat is to be used concomitantly with CYP450 3A4 inducers.', 'Metabolism', 'Caution and clinical monitoring is recommended if tazemetostat is to be used concomitantly with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
