INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1492556, 'Gefitinib', 'Tazemetostat', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of gefitinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Pharmacologic response to gefitinib should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy. In patients receiving potent inducers such as rifampin or phenytoin, gefitinib product labeling states that a dosage increase to 500 mg daily should be considered in the absence of severe adverse reactions, and additional dosage adjustments be made based on clinical response. Gefitinib dosage should be decreased accordingly if these agents are discontinued.', 'Metabolism', 'Pharmacologic response to gefitinib should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
