INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1492558, 'Glasdegib', 'Tazemetostat', 'Moderate', 'Coadministration with glasdegib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) efflux transporters.', 'DDInter', 1767317477, 'Caution is advised if glasdegib is coadministered with drugs that are substrates of P-gp and/or BCRP, particularly those with a narrow therapeutic range. Clinical and laboratory monitoring should be considered whenever glasdegib is added to or withdrawn from therapy with these drugs, and dosages adjusted as necessary. Patients should be monitored for the development of adverse effects.', 'Absorption', 'Caution is advised if glasdegib is coadministered with drugs that are substrates of P-gp and/or BCRP, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
