INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1492570, 'Indomethacin', 'Tazemetostat', 'Moderate', 'Systemically and topically administered nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of bleeding in patients treated with anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The pharmacologic effects of NSAIDs that contribute to this interaction include prolongation of prothrombin time and inhibition of platelet adhesion and aggregation.', 'DDInter', 1767317477, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1492571, 'Tositumomab (I-131)', 'Tazemetostat', 'Major', 'Coadministration of tositumomab and iodine I 131 tositumomab with drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Tositumomab and iodine I 131 tositumomab alone can cause severe or life-threatening thrombocytopenia.', 'DDInter', 1767317477, 'Due to the frequent occurrence of severe and prolonged thrombocytopenia associated with tositumomab and iodine I 131 tositumomab, concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously. Close clinical and laboratory observation for bleeding complications is recommended during and after the therapeutic regimen.', 'Synergism', 'Due to the frequent occurrence of severe and prolonged thrombocytopenia associated with tositumomab and iodine I 131 tositumomab, concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1492572, 'Irinotecan (liposomal)', 'Tazemetostat', 'Moderate', 'Coadministration with inducers of the CYP450 3A4 isoenzyme may decrease the plasma concentrations of irinotecan and its pharmacologically active metabolite, SN-38. Irinotecan is partially metabolized by CYP450 3A4, and induction of this process results in less of the drug available in the plasma for conversion to SN-38 via carboxylesterases..', 'DDInter', 1767317477, 'The antitumour activity of irinotecan may be reduced in patients treated with CYP450 3A4 inducers. Pharmacologic response to irinotecan should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the irinotecan dosage adjusted as necessary.', 'Metabolism', 'The antitumour activity of irinotecan may be reduced in patients treated with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
