INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1492668, 'Heparin', 'Telavancin', 'Major', 'Although telavancin does not interfere with coagulation, it may artificially prolong activated partial thromboplastin time (aPTT) test results for up to 18 hours following its administration. Telavancin binds to the artificial phospholipid surfaces added to common anticoagulation tests, thereby interfering with the ability of the coagulation complexes to assemble on the surface of the phospholipids and promote clotting in vitro.', 'DDInter', 1767317477, 'Concomitant use of telavancin and intravenous unfractionated heparin sodium is considered contraindicated. For patients who require aPTT monitoring while being treated with telavancin, a non-phospholipid dependent coagulation test such as a Factor Xa (chromogenic) assay or an alternative anticoagulant not requiring aPTT monitoring may be considered.', 'Antagonism', 'Concomitant use of telavancin and intravenous unfractionated heparin sodium is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
