INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1494789, 'Tiagabine', 'St. John''s Wort', 'Moderate', 'Coadministration with St. John''s wort may significantly decrease the plasma concentrations and therapeutic effects of tiagabine. The proposed mechanism is thought to be induction of CYP450 3A4-mediated metabolism of tiagabine by St. John''s wort. The plasma concentration of tiagabine has been reduced by a factor of 1.5 to 3 following concomitant use with other potent inducers of CYP450 3A4 such as carbamazepine and phenytoin.', 'DDInter', 1767317477, 'Dosage adjustments as well as clinical and laboratory monitoring may be necessary if these medications are used together, especially if St. John''s wort is started or stopped in a patient stabilized on tiagabine. Alternative therapies may also be considered. Use of St. John''s wort with tiagabine is considered contraindicated by some authorities. Patients should be advised to notify their doctor if they experience loss of seizure control.', 'Metabolism', 'Dosage adjustments as well as clinical and laboratory monitoring may be necessary if these medications are used together, especially if St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
