        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1494994, 'Heparin', 'Ticlopidine', 'Moderate', 'Although therapy with heparin and ticlopidine often overlaps in clinical practice, the potential for additive anticoagulant effects and increased risk of bleeding should be considered.', NULL, 'The manufacturer recommends that heparin be discontinued before initiating ticlopidine therapy.', NULL, 'The manufacturer recommends that heparin be discontinued before initiating ticlopidine therapy. Patients who are to receive these drugs concomitantly should be closely monitored for signs of bleeding. Clinical trials have reported the concomitant use of heparin and ticlopidine in patients undergoing coronary stenting.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
