    ) VALUES (1495008, 'Ticlopidine', 'Oxtriphylline', 'Moderate', 'In one study, theophylline clearance was reduced by 37% and theophylline half-life increased by 42% during concomitant ticlopidine therapy. The exact mechanism of this interaction is not known, but ticlopidine probably interferes with theophylline metabolism.', NULL, 'Theophylline levels and clinical response should be monitored when ticlopidine therapy is initiated or discontinued.', NULL, 'Theophylline levels and clinical response should be monitored when ticlopidine therapy is initiated or discontinued. Patients should be advised to report any signs of theophylline toxicity during concurrent therapy, including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat to their physician.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
