    ) VALUES (1495115, 'Timolol', 'Arbutamine', 'Moderate', 'Beta-blockers will attenuate the desired effects of arbutamine.', NULL, 'Beta-blockers should be discontinued at least 48 hours before arbutamine is administered.', NULL, 'Beta-blockers should be discontinued at least 48 hours before arbutamine is administered.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
