    ) VALUES (1495174, 'Glucagon', 'Timolol', 'Minor', 'Beta-blockers may blunt the hyperglycemic reaction to glucagon by interfering with beta-adrenergic-mediated hyperglycemic counter-regulatory mechanisms.', NULL, '-', NULL, '-', 'Antagonism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
